<?	if(isset($_SESSION['name']))	{		//Обработка данных и добавление их в БД		if($_SERVER['REQUEST_METHOD'] == "POST" && $_POST['add'] == "Добавить")		{			$idKingdom = CleanData($_POST['kingdom'], 'i');			$idType = CleanData($_POST['type'], 'i');			$idClass = CleanData($_POST['class'], 'i');			$nameRus = CleanData($_POST['nameRus']);			$nameLat = CleanData($_POST['nameLat']);			if($nameRus != "" && $nameLat != "")			{				//Проверка размера изображения и типа изображения				if($_FILES['photo']['size'] != 0 && $_FILES['photo']['size'] <= 1024000)				{					//Проверка типа изображения					if($_FILES['photo']['type'] == "image/jpeg")					{						$photoName = $nameLat . ".jpg";						$uploaddir = 'image/detachments/';						$uploadfile = $uploaddir . $photoName;						//Копирование изображения						move_uploaded_file($_FILES['photo']['tmp_name'], $uploadfile);												$result = AddDetachment($idKingdom, $idType, $idClass, $nameRus, $nameLat, $photoName);						if(isset($result) && $result != false)						{							AddSuccessMessage("Запись об отряде успешно добавлена");						}					}				}				if($_FILES['photo']['name'] == "")				{					$result = AddDetachment($idKingdom, $idType, $idClass, $nameRus, $nameLat);					if(isset($result) && $result != false)					{						AddSuccessMessage("Запись об отряде успешно добавлена");					}				}			}			if($_FILES['photo']['name'] == "")			{				AddErrors($nameRus, $nameLat);			}			else			{				AddErrors($nameRus, $nameLat, $_FILES['photo']['size'], $_FILES['photo']['type']);			}						echo "<script>museum.redirect('Add', 'add_detachment');</script>";		}	}?><div id='titlesus'>	<table>		<tr>			<td><img src='image/system/add_data.png' width='37px' height='40px' class='add_data'></td>			<td><span class='titlesus_h'>Менеджер экспонатов: добавить отряд</span></td>		</tr>	</table></div><?	if(isset($_SESSION['name']))	{		if($_SERVER['REQUEST_METHOD'] != "POST")		{			ShowErrors();			ShowSucce