<?	if(isset($_SESSION['name']))	{		//Обработка данных и добавление их в БД		if($_SERVER['REQUEST_METHOD'] == "POST" && $_POST['add'] == "Добавить")		{			$idKingdom = CleanData($_POST['kingdom'], 'i');			$nameRus = CleanData($_POST['nameRus']);			$nameLat = CleanData($_POST['nameLat']);			if($nameRus != "" && $nameLat != "")			{				$result = AddType($idKingdom, $nameRus, $nameLat);								if(isset($result) && $result != false)				{									AddSuccessMessage("Запись о типе успешно добавлена.");				}			}			AddErrors($nameRus, $nameLat);						echo "<script>museum.redirect('Add', 'add_type');</script>";		}	}?><div id='titlesus'>	<table>		<tr>			<td><img src='image/system/add_data.png' width='37px' height='40px' class='add_data'></td>			<td><span class='titlesus_h'>Менеджер экспонатов: добавить тип</span></td>		</tr>	</table></div><?	if(isset($_SESSION['name']))	{		if($_SERVER['REQUEST_METHOD'] != "POST")		{			ShowErrors();			ShowSuccessMessage();		}		$sql = "SELECT id, namerus, namelat FROM carstva";		$result = mysql_query($sql) or die(mysql_error());?>		<form name = "addType" action = "index.php?actionAdd=add_type" method = "POST">			<div id='cont'>				<fieldset class='fs'>				<legend><span class='legend'>Добавление экспоната</span></legend>				<table>					<tr class='asdasd'>						<td class='number1'>Царство<span class='star'>*</span></td>						<td><select name = "kingdom" class='ttext'>					<?					while($rowKingdom = mysql_fetch_assoc($result))					{						echo "<option value = {$rowKingdom['id']}>{$rowKingdom['namerus']} | {$rowKingdom['namelat']}";					}					?>						</select></td>					</tr>					<tr class='asdasd'>						<td class='number1'>Русское название<span class='star'>*</span></td>						<td><input type = "text" name = "nameRus" class='ttext'></p></td>					</tr>					<tr class='asdasd'>						<td class='number1'>Латинское название<span class='star'>*</span></td>						<td><input type = "text" name = "n