<?		if(isset($_SESSION['name']))	{				if($_SERVER['REQUEST_METHOD'] == "POST" && $_POST['Ok'] == "Изменить")		{			//Очистка переданых данных			$idKingdom = CleanData($_POST['kingdom'], 'i');			$idType = CleanData($_POST['type'], 'i');			$nameRus = CleanData($_POST['nameRus']);			$nameLat = CleanData($_POST['nameLat']);			$id = CleanData($_POST['id'], 'i');			//Внесение изменений в БД			if($nameRus != "" && $nameLat != "")			{				ChangeClass($id, $idKingdom, $idType, $nameRus, $nameLat);				header("Location: index.php?actionChangeAll=change_class_all");			}			AddErrors($nameRus, $nameLat);			header("Location: index.php?actionChangeAll=change_class");		}	}?>