<?	if(isset($_SESSION['name']))	{				if($_SERVER['REQUEST_METHOD'] == "POST" && $_POST['Ok'] == "Изменить")		{			//Очистка переданых данных			$idKingdom = CleanData($_POST['kingdom'], 'i');			$idType = CleanData($_POST['type'], 'i');			$idClass = CleanData($_POST['class'], 'i');			$nameRus = CleanData($_POST['nameRus']);			$nameLat = CleanData($_POST['nameLat']);			$id = CleanData($_POST['id'], 'i');			//Внесение изменений в БД			if($nameRus != "" && $nameLat != "")			{				//Проверка размера изображения				if($_FILES['photo']['size'] != 0 && $_FILES['photo']['size'] <= 1024000)				{					//Проверка типа изображения					if($_FILES['photo']['type'] == "image/jpeg")					{												$photoName = $nameLat.".jpg";						$uploaddir = 'image/detachments/';						$uploadfile = $uploaddir . $photoName;						//Копирование изображения						move_uploaded_file($_FILES['photo']['tmp_name'], $uploadfile);							ChangeDetachment($id, $idKingdom, $idType, $idClass, $nameRus, $nameLat, $photoName);						header("Location: index.php?actionChangeAll=change_detachment");					}				}				if($_FILES['photo']['name'] == "")				{					ChangeDetachment($id, $idKingdom, $idType, $idClass, $nameRus, $nameLat);					header("Location: index.php?actionChangeAll=change_detachment");				}			}				if($_FILES['photo']['name'] == "")			{				AddErrors($nameRus, $nameLat);			}			else			{				AddErrors($nameRus, $nameLat, $_FILES['photo']['size'], $_FILES['photo']['type']);			}			header("Location: index.php?actionChangeAll=change_detachment");		}	}?>