<?	if(isset($_SESSION['name']))	{				if($_SERVER['REQUEST_METHOD'] == "POST" && $_POST['Ok'] == "Изменить")		{			//Очистка переданых данных			$idKingdom = CleanData($_POST['kingdom'], 'i');			$idType = CleanData($_POST['type'], 'i');			$idClass = CleanData($_POST['class'], 'i');			$idDetachment = CleanData($_POST['detachment'], 'i');			$nameRus = CleanData($_POST['nameRus']);			$nameLat = CleanData($_POST['nameLat']);			$id = CleanData($_POST['id'], 'i');			//Внесение изменений в БД			if($nameRus != "" && $nameLat != "")			{				$result = ChangeFamily($id, $idKingdom, $idType, $idClass, $idDetachment, $nameRus, $nameLat);								if(isset($result) && $result != false)				{					AddSuccessMessage("Информация о семействе успешно изменена.");				}			}			AddErrors($nameRus, $nameLat);?>			<script>museum.redirect('Change', 'change_family_all');</script><?		}	}?><div id='titlesus'>	<table>		<tr>			<td><img src='image/system/add_data.png' width='37px' height='40px' class='add_data'></td>			<td><span class='titlesus_h'>Менеджер экспонатов: изменить семейство</span></td>		</tr>	</table></div><?		if(isset($_SESSION['name']))	{		//Заполнение полей формы текущими значениями		if($_SERVER['REQUEST_METHOD'] == "POST" && $_POST['Ok'] == "Выбрать")		{			$sqlKingdom = "SELECT id, namerus, namelat FROM carstva";			$resultKingdom = mysql_query($sqlKingdom) or die(mysql_error());			$sqlType = "SELECT id, namerus, namelat FROM tip";			$resultType = mysql_query($sqlType) or die(mysql_error());			$sqlClass = "SELECT id, namerus, namelat FROM klass";			$resultClass = mysql_query($sqlClass) or die(mysql_error());			$sqlDetachment = "SELECT id, namerus, namelat FROM otrjad";			$resultDetachment = mysql_query($sqlDetachment) or die(mysql_error());			$id = CleanData($_POST["familySelected"], 'i');			$sqlFamily = "SELECT id, idcar, idtip, idklass, idotr, namerus, namelat FROM semejstva WHERE id = {$id}";			$resultFamily = mysql_query($sqlFamily)