							}									?>									</select>								</td>							</tr>							<tr class='asdasd'>								<td class='number1'>Русское название типа<span class='star'>*</span></td>								<td><input type = "text" class='ttext' name = "nameRus" value = "<?=$family['namerus']?>" required></td>							</tr>							<tr class='asdasd'>								<td class='number1'>Латинское название типа<span class='star'>*</span></td>								<td><input type = "text" class='ttext' name = "nameLat" value = "<?=$family['namelat']?>" required></td>							</tr>						</table>					</fieldset>					<input type = "hidden" name = "id" value = "<?=$id?>">					<input type = "submit" name = "Ok" value = "Изменить" class="buttonw">					<input type = "button" name = "back" value = "Назад" class="buttonw" onclick = "museum.redirect('Change', 'change_family_all')">				</div>			</form><?		}	}?>