<?		if(isset($_SESSION['name']))	{				if($_SERVER['REQUEST_METHOD'] == "POST" && $_POST['Ok'] == "Изменить")		{			//Очистка переданых данных			$idKingdom = CleanData($_POST['kingdom'], 'i');			$idType = CleanData($_POST['type'], 'i');			$idClass = CleanData($_POST['class'], 'i');			$idDetachment = CleanData($_POST['detachment'], 'i');			$idFamily = CleanData($_POST['family'], 'i');			$nameRus = CleanData($_POST['nameRus']);			$nameLat = CleanData($_POST['nameLat']);			$id = CleanData($_POST['id'], 'i');			//Внесение изменений в БД			if($nameRus != "" && $nameLat != "")			{				ChangeSpecies($id, $idKingdom, $idType, $idClass, $idDetachment, $idFamily, $nameRus, $nameLat);				header("Location: index.php?actionChangeAll=change_species");			}				AddErrors($nameRus, $nameLat);			header("Location: index.php?actionChangeAll=change_species");		}	}?>