<?	//Хранит имя хоста	define("HOST", "localhost");	//Хранит имя пользователя с правами на доступ	define("USER", "root");	//Хранит пароль этого пользователя	define("PASSWORD", "");	//Хранит имя базы данных	define("DB", "muz");	//Подключение к хосту	$db = mysql_connect(HOST, USER, PASSWORD) or die(mysql_error());	//Выбор базы данных	mysql_select_db(DB, $db) or die(mysql_error());?>