<?	if(isset($_SESSION['name']))	{		//Обработка данных и добавление их в БД		if($_SERVER['REQUEST_METHOD'] == "POST" && $_POST['add'] == "Добавить")		{						$idKingdom = CleanData($_POST['kingdom'], 'i');			$idType = CleanData($_POST['type'], 'i');			$idClass = CleanData($_POST['class'], 'i');			$nameRus = CleanData($_POST['nameRus']);			$nameLat = CleanData($_POST['nameLat']);			if($nameRus != "" && $nameLat != "")			{								//Проверка размера изображения и типа изображения				if($_FILES['photo']['size'] != 0 && $_FILES['photo']['size'] <= 1024000)				{									//Проверка типа изображения					if($_FILES['photo']['type'] == "image/jpeg")					{						$photoName = $nameLat.".jpg";						$uploaddir = '../../image/detachments/';						$uploadfile = $uploaddir . $photoName;						//Копирование изображения						move_uploaded_file($_FILES['photo']['tmp_name'], $uploadfile);												AddDetachment($idKingdom, $idType, $idClass, $nameRus, $nameLat, $photoName);						header("Location: index.php?action=add_detachment");					}				}				if($_FILES['photo']['name'] == "")				{					AddDetachment($idKingdom, $idType, $idClass, $nameRus, $nameLat);					header("Location: index.php?actionAdd=add_detachment");				}			}			if($_FILES['photo']['name'] == "")			{				AddErrors($nameRus, $nameLat);			}			else			{				AddErrors($nameRus, $nameLat, $_FILES['photo']['size'], $_FILES['photo']['type']);			}		}	}?>