<?	if(isset($_SESSION['name']))	{		//Обработка данных и добавление их в БД		if($_SERVER['REQUEST_METHOD'] == "POST" && $_POST['add'] == "Добавить")		{						$idKingdom = CleanData($_POST['kingdom'], 'i');			$idType = CleanData($_POST['type'], 'i');			$idClass = CleanData($_POST['class'], 'i');			$idDetachment = CleanData($_POST['detachment'], 'i');			$nameRus = CleanData($_POST['nameRus']);			$nameLat = CleanData($_POST['nameLat']);			if($nameRus != "" && $nameLat != "")			{				AddFamily($idKingdom, $idType, $idClass, $idDetachment, $nameRus, $nameLat);				header("Location: index.php?actionAdd=add_family");			}					AddErrors($nameRus, $nameLat);		}	}?>