<div id='titlesus'>				<table>					<tr>						<td><img src='image/system/add_data.png' width='37px' height='40px' class='add_data'></td>						<td><span class='titlesus_h'>Менеджер экспонатов: удалить экспонат</span></td>					</tr>				</table></div><?	if(isset($_SESSION['name']))	{		//Выборка всех экспонатов		$sqlExhibit = "SELECT id, vid FROM eksponat";		$resultExhibit = mysql_query($sqlExhibit) or die(mysql_error());?>		<div id='cont'>		<form name = "deleteExhibit" action = "index.php?actionDelete=delete_exhibit" method = "POST">		<fieldset class='fs'>		<legend><span class='legend'>Удаление экспоната</span></legend>		<table>		<?			$count = 0;			//Вывод всех экспонатов			while($rowExhibit = mysql_fetch_assoc($resultExhibit))			{				$sqlSpecies = "SELECT namerus, namelat FROM vid WHERE id = {$rowExhibit['vid']}";				$resultSpecies = mysql_query($sqlSpecies) or die(mysql_error());				$species = mysql_fetch_assoc($resultSpecies);				if($count == 0)				{					echo"<tr class='asdasd'><td><input type = 'radio' name = 'selectedExhibit' value = {$rowExhibit['id']} checked></td><td>[{$rowExhibit['id']}]{$species['namerus']} | {$species['namelat']}</td></tr>";				}				else				{					echo"<tr class='asdasd'><td><input type = 'radio' name = 'selectedExhibit' value = {$rowExhibit['id']}></td><td>[{$rowExhibit['id']}]{$species['namerus']} | {$species['namelat']}</td></tr>";				}				$count++;			}					?>						</table>			<input type = "submit" name = "Delete" value = "Удалить"class="buttonw">		</fieldset>		</form>		</div><?	}?>